import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Container, Card, Button, Menu } from 'semantic-ui-react'
import PostCard from './PostCard'
import PostForm from './PostForm'
import CardDetail from './CardDetail'
import { BrowserRouter } from 'react-router-dom';
import { Route, Link, Switch, Redirect} from 'react-router-dom'

class App extends React.Component {
  state = {
    posts: null,
    welcome: true,
    allPosts: false,
    homePage: true,
    showForm: false,
    itemsPerRow: 3,
    showMenu: false,
    showDetail: false,
    detailPost: null
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(json => this.setState({posts: json}))
  }

  increase = () => {
    console.log("increase")
    if (this.state.itemsPerRow > 7) {
      return null
    }
    this.setState({itemsPerRow: this.state.itemsPerRow + 1})
  }
  decrease = () => {
    if (this.state.itemsPerRow  < 2) {
      return null
    }
    console.log("decrease")
    this.setState({itemsPerRow: this.state.itemsPerRow - 1})
  }

  showPosts = () => {
    this.setState({
      homePage: false,
      allPosts: true,
      showMenu: true
    })
  }

  showForm = () => {
    this.setState({
      allPosts: !this.state.allPosts,
      showForm: !this.state.showForm
    })
  }

  showDetail = (e, post) => {
    console.log(e, post)
    this.setState({
      allPosts: !this.state.allPosts,
      showDetail: !this.state.showDetail,
      detailPost: post
    })
  }

  addNewPost = ( post) => {
    let o = this.state.posts
    let b = [...this.state.posts, post]
    this.setState({ posts: b})
    this.showForm()
  }

  render() {
    return (


      <BrowserRouter>

        <Switch>
          <Route path='/' render={() => {
            return(
            <div><h1 style={{ 'textAlign': 'center', 'fontSize': '12em' }} > Welcome to Poster  App</h1>
              <Button
                fluid
                onClick={this.showPosts} >
                Click to continue to posts
            </Button> </div> )
            }}

          />
        </Switch>
      <Container>
        {this.state.showMenu ?
          <Menu pointing secondary>
            <Menu.Item>
              <Button>All Posts</Button>
            </Menu.Item>
            <Menu.Item >
              <Button
                onClick={this.showForm}
              > Create A Post</Button>
            </Menu.Item>
            <Menu.Item fluid>
              <h1>Application About Posting Things</h1>
            </Menu.Item>
            <Menu.Item>
              <Button onClick={this.increase}>+</Button>
            </Menu.Item>
            <Menu.Item>
              <Button onClick={this.decrease}>-</Button>
            </Menu.Item>
          </Menu> : null}

        {this.state.allPosts ? <Card.Group itemsPerRow={this.state.itemsPerRow}>
          {this.state.posts ? this.state.posts.map(post => <PostCard
            showDetail={this.showDetail}
            post={post} />) : <Card raised><h1>Loading...</h1></Card>}
        </Card.Group> : null}

        {/* {this.state.homePage ? <div><h1 style={{ 'textAlign': 'center', 'fontSize': '12em' }} > Welcome to Poster  App</h1>
          <Button
            fluid
            onClick={this.showPosts} >
            Click to continue to posts
        </Button> </div>: null} */}


        {this.state.showForm ? <PostForm addNewPost={this.addNewPost}/> : null}

        {this.state.showDetail ? <CardDetail showDetail={this.showDetail} post={this.state.detailPost}/> : null}

        </Container>
      </BrowserRouter>


    )
  }

}

export default App;
